BlackBox.template:
// blockRamFile begin
reg ~TYPO ~GENSYM[RAM][0] [0:~LIT[3]-1];

initial begin
  $readmemb(~FILE[~LIT[4]],~SYM[0]);
end
~IF ~ISGATED[2] ~THEN
always @(posedge ~ARG[2][1]) begin : ~GENSYM[~COMPNAME_blockRamFile][2]~IF ~VIVADO ~THEN
  if (~ARG[2][0]) begin
    if (~ARG[6]) begin
      ~SYM[0][~ARG[7]] <= ~ARG[8];
    end
    ~RESULT <= ~SYM[0][~ARG[5]];
  end~ELSE
  if (~ARG[6] & ~ARG[2][0]) begin
    ~SYM[0][~ARG[7]] <= ~ARG[8];
  end
  if (~ARG[2][0]) begin
    ~RESULT <= ~SYM[0][~ARG[5]];
  end~FI
end~ELSE
always @(posedge ~ARG[2]) begin : ~SYM[2]
  if (~ARG[6]) begin
    ~SYM[0][~ARG[7]] <= ~ARG[8];
  end
  ~RESULT <= ~SYM[0][~ARG[5]];
end~FI
// blockRamFile end
