-- replaceBit begin
replaceBit_~SYM[0] : block
  signal vec       : ~TYP[1];
  signal vec_index : integer range 0 to ~LIT[0]-1;
  signal din       : ~TYP[3];
begin
  vec <= ~ARG[1];
  vec_index <= to_integer(~ARG[2])
  -- pragma translate_off
               mod ~LIT[0]
  -- pragma translate_on
               ;
  din <= ~ARG[3];

  process(vec,vec_index,din)
    variable ivec : ~TYP[1];
  begin
    ivec := vec;
    ivec(vec_index downto vec_index) := din;
    ~RESULT <= ivec;
  end process;
end block;
-- replaceBit end

-- setSlice begin
setSlice_~SYM[0] : block
  signal ~SYM[1] : ~TYP[0];
  signal ~SYM[2] : ~TYP[3];
begin
  ~SYM[1] <= ~ARG[0];
  ~SYM[2] <= ~ARG[3];
  process(~SYM[1],~SYM[2])
    variable ~SYM[3] : ~TYP[0];
  begin
    ~SYM[3] := ~SYM[1];
    ~SYM[3](~LIT[1] downto ~LIT[2]) := ~SYM[2];
    ~RESULT <= ~SYM[3];
  end process;
end block;
