BlackBox.templateD:
-- divSigned begin
divSigned_~SYM[0] : block
  signal ~SYM[1] : ~TYP[0];
  signal ~SYM[2] : ~TYP[1];
  signal ~SYM[3] : ~TYP[0];
begin
  ~SYM[1] <= ~ARG[0];
  ~SYM[2] <= ~ARG[1];
  ~SYM[3] <= ~SYM[1] / ~SYM[2];
  ~RESULT <= ~SYM[3] - to_signed(1,~SIZE[~TYPO]) when ~SYM[1](~SYM[1]'high) = not (~SYM[2](~SYM[2]'high)) else
             ~SYM[3];
end block;
-- divSigned end

-- truncateB begin
truncateB_~SYM[0] : block
  signal ~SYM[1] : ~TYP[1];
begin
  ~SYM[1] <= ~ARG[1];
  ~RESULT <= ~SYM[1](~LIT[0]-1 downto 0);
end block;
-- truncateB end
